import{_ as t,c as a,o,aj as n}from"./chunks/framework.B72pWmSA.js";const h=JSON.parse('{"title":"What is Swagger-PHP?","description":"","frontmatter":{},"headers":[],"relativePath":"guide/index.md","filePath":"guide/index.md"}'),i={name:"guide/index.md"};function s(r,e,d,c,p,l){return o(),a("div",null,[...e[0]||(e[0]=[n('<h1 id="what-is-swagger-php" tabindex="-1">What is Swagger-PHP? <a class="header-anchor" href="#what-is-swagger-php" aria-label="Permalink to &quot;What is Swagger-PHP?&quot;">​</a></h1><p><code>swagger-php</code> is a library that extracts API metadata from your PHP source code files.</p><p>The idea is to add <code>swagger-php</code> <a href="./using-annotations.html">annotations</a> or <a href="./using-attributes.html">attributes</a> next to the relevant PHP code in your application. These will contain the details about your API and <code>swagger-php</code> will convert those into machine-readable <a href="https://spec.openapis.org/oas/v3.1.0.html" target="_blank" rel="noreferrer">OpenAPI documentation</a>.</p><p>By adding your API documentation next to the corresponding source code (same file!) makes it easy to keep it up-to-date as all details can be modified in one place.</p><div class="tip custom-block"><p class="custom-block-title">Annotating vs. Annotations</p><p>When talking about annotating your code we mean the act of adding meta-data to your codebase. This can be done by either adding <a href="./using-annotations.html"><code>Annotations</code></a> or <a href="./using-attributes.html"><code>Attributes</code></a>.</p></div><div class="warning custom-block"><p class="custom-block-title">Requirements</p><p>Using <code>swagger-php</code> requires a minimum of <strong>PHP 7.4</strong> for using annotations and at least <strong>PHP 8.1</strong> to use attributes.</p></div>',6)])])}const u=t(i,[["render",s]]);export{h as __pageData,u as default};
